import React,{Component} from 'react';
import { If, Then, ElseIf } from 'react-if-elseif-else-render';
import './employee.css';
import axios from 'axios';
class Employee extends Component{

    constructor(){
        super()
        this.state = {
            firstName: "",
            lastName: "",
            email: "",
            phone:"",
            gender:"",
            empid: 0,
            empDetailsRead:[],
            readEmpDetails:[],
            buttontext: "Create"
        }
        this.handleCreate = this.handleCreate.bind(this);
        this.handleRead = this.handleRead.bind(this);
        this.handleUpdate = this.handleUpdate.bind(this);
        this.handleDelete = this.handleDelete.bind(this);
        this.handleOnChange = this.handleOnChange.bind(this);
        this.FinalAction = this.FinalAction.bind(this);
        this.UpdateEmployeeDetails = this.UpdateEmployeeDetails.bind(this);
        this.DeleteEmployeeDetails = this.DeleteEmployeeDetails.bind(this);
        this.FetchEmpDetailsbyID = this.FetchEmpDetailsbyID.bind(this);
    }

    handleReadEmpDetails (empDetail) {
        console.log('employee.js file handleReadEmpDetails() empDetail : ',empDetail);
        let empDetails = this.state.readEmpDetails;
        empDetails.push(empDetail);
        this.setSate({readEmpDetails : empDetails});
    }
    handleCreate(){
        this.setState({
            buttontext :   "Create"
        })
    }
    handleRead(){
        this.setState({
            buttontext :   "Read"
        })
    }
    handleUpdate(){
        this.setState({
            buttontext :   "Update"
        })
    }
    handleDelete(){
        this.setState({
            buttontext :   "Delete"
        })
    }
    handleOnChange(event){
        this.setState({
            [event.target.name] : event.target.value
        })
    }
    UpdateEmployeeDetails(){
        console.log('UpdateEmployeeDetails Start');
        axios.put(`/api/employee/UpdateEmpDetails/${this.state.empid}/${this.state.firstName}/${this.state.lastName}/${this.state.email}/${this.state.phone}/${this.state.gender}`)
        .then (function(response){
            console.log('Update Response.status: ', Response.status);
            console.log('Update Response.statusText: ', Response.statusText);

            if (response.status === 200 || response.statusText.toLowerCase() === "ok"){
                return console.log("Update Employee by Details Success");
            }else{
                return console.log('Update Employee Details Failed');
            }
        })
        .catch(error => {
            return console.log('UpdateEmployee By Details Failed with Error :', error);
        })
    }
    DeleteEmployeeDetails(){
        console.log('Deleting the Employee Details  Start');
        axios.delete(`/api/employee/DeleteEmpDetails/${this.state.empid}`)
        .then(function(response){
            console.log('Delete Response.status: ', Response.status);
            console.log('Delete Response.statusText: ', Response.statusText);

            if (response.status === 200 || response.statusText.toLowerCase() === "ok"){
                return console.log("Delete Employee by Details Success");
            }else{
                return console.log('Delete Employee Details Failed');
            }
        })
        .catch(error => {
            console.log('DeleteEmployee Failed with Error :',error);
        });
    }
    FetchEmpDetailsbyID(){
        console.log('FetchEmpDetailsbyID Start empID',this.state.empid);
        if ( this.state.empid === '')
        {
            alert('Emp ID is required')            ;
        }
        else{
            axios.get('/api/employee/getEmpID/',  {
                                'params': { 
                                        empid: this.state.empid
                                        }
                                })
                                .then(function (Response){
                                    console.log('Get Response.status: ', Response.status);
                                    console.log('Get Response.statusText: ', Response.statusText);
                                  if (Response.status === 200 || Response.statusText.toLowerCase() === "ok"){
                                        console.log('data Item: ', Response.data.Item);
                                        console.log('FirstName :', Response.data.Item.FirstName);
                                        console.log('LastName :', Response.data.Item.LastName);
                                        console.log('email :', Response.data.Item.Email);
                                        console.log('Phone :', Response.data.Item.Phone);
                                        console.log('Gender :', Response.data.Item.Gender);
                                        //this.setState({readEmpDetails : Response.data.Item});   
                                        //console.log('readEmpDetails :',this.state.readEmpDetails);  
                                        this.setState({[this.statefirstName] : Response.data.Item.FirstName});
                                        this.setState({[this.state.lastName] : Response.data.Item.LastName});
                                        this.setState({[this.state.email] : Response.data.Item.Email});
                                        this.setState({[this.state.phone] : Response.data.Item.Phone});
                                        this.setState({[this.state.gender] : Response.data.Item.Gender});
                                        console.log('FirstName: ', this.state.firstName);
                                        console.log('LastName: ', this.state.lastName);
                                        console.log('Phone: ', this.state.phone);
                                        console.log('Email: ', this.state.email);
                                        console.log('Gender: ', this.state.gender);
                                        return console.log('Read Employee Details by ID Success');
                                    }
