import React,{Component} from 'react';
import './employee.css';

class Employee extends Component{

    constructor(){
        super()
        this.state = {
            firstName: "",
            lastName: "",
            email: "",
            phone:"",
            gender:"",
            buttontext: ""
        }
        this.handleCreate = this.handleCreate.bind(this);
        this.handleRead = this.handleRead.bind(this);
        this.handleUpdate = this.handleUpdate.bind(this);
        this.handleDelete = this.handleDelete.bind(this);
        this.handleOnChange = this.handleOnChange.bind(this);
        this.FinalAction = this.FinalAction.bind(this);
    }
    handleCreate(){

    }
    handleRead(){

    }
    handleUpdate(){

    }
    handleDelete(){

    }
    handleOnChange(event){
        
    }
    render() {
        return(
           <div className="main">
                <h1 className="heading1"> Employee Management </h1>
                <h4 className="heading4"> Open Book Assignment Submitted by Gomathi T</h4>
                <button className="button" onClick={this.handleCreate}>Create</button>
                <button className="button" onClick={this.handleRead}>Read</button>
                <button className="button" onClick={this.handleUpdate}>Update</button>
                <button className="button" onClick={this.handleDelete}>Delete</button>
                <hr/>

                <h2 className="heading2"> Create New Employee </h2>
                <hr className="hr" />
                <label 
                    className="label" >First Name 
                    <input 
                        type="text" 
                        className="input"
                        name="firstName" 
                        value={this.state.firstName} 
                        placeholder="FirstName" 
                        onChange={this.handleOnChange} />
                </label>
                <br />
                <label 
                    className="label">Last Name   
                    <input 
                        type="text" 
                        name="lastName" 
                        className="input"
                        value={this.state.lastName} 
                        placeholder="LastName" 
                        onChange={this.handleOnChange} />
                </label>
                <br />
                <label 
                    className="label">Email  
                    <input 
                        type="email" 
                        name="email" 
                        className="input"
                        value={this.state.email} 
                        placeholder="example@domain.com" 
                        onChange={this.handleOnChange} />
                </label>
                <br />
                <label 
                    className="label">Phone  
                    <input 
                        type="tel" 
                        name="phone" 
                        className="input"
                        value={this.state.phone} 
                        placeholder="984325765" 
                        onChange={this.handleOnChange} />
                </label>
                <br />
                <label 
                    className="label">Gender  </label>
                <label
                    className="label">    
                    <input 
                        type="radio" 
                        name="gender" 
                        value="male" 
                        checked={this.state.gender === "male"}
                        onChange={this.handleOnChange}/> Male
                </label>
                <label
                    className="label">    
                    <input 
                        type="radio" 
                        name="gender" 
                        value="female" 
                        checked={this.state.gender === "female"}
                        onChange={this.handleOnChange}/> Female
                </label>

            <button 
                className="button"
                onClick={this.FinalAction}>{this.state.buttontext}</button>
           </div> 
        );
    }
}

export default Employee;
