import React,{Component} from 'react';
import './employee.css';

class Employee extends Component{

    constructor(){
        super()
        this.state = {
            firstName: "",
            lastName: "",
            email: "",
            phone:"",
            gender:""
        }
    }
    render() {
        return(
           <div className="main">
                <h1 className="heading1"> Employee Management </h1>
                <h4 className="heading4"> Open Book Assignment Submitted by Gomathi T</h4>
                <button className="button" onClick={this.handleNewEmp}>Create</button>
                <button className="button" onClick={this.handleRead}>Read</button>
                <button className="button" onClick={this.handleUpdate}>Update</button>
                <button className="button" onClick={this.handleDelete}>Delete</button>
                <hr/>

                <h2 className="heading2"> Create New Employee </h2>
                <hr className="hr" />
                <label 
                    className="label" >First Name 
                    <input 
                        type="text" 
                        className="input"
                        name="firstName" 
                        value={this.state.firstName} 
                        placeholder="FirstName" 
                        onChange={this.handleOnChange} />
                </label>
                <br />
                <label 
                    className="label">Last Name 
                    <input 
                        type="text" 
                        name="lastName" 
                        style={{marginLeft : 50}} 
                        value={this.state.lastName} 
                        placeholder="LastName" 
                        onChange={this.handleOnChange} />
                </label>
           </div> 
        );
    }
}

export default Employee;
