import React,{Component} from 'react';
import axios from 'axios';

class EmployeeID extends Component
{
    constructor(){
        super()
        this.state = {
            empid: 0,
            empDetailsRead:[]
        }
        this.handleOnChange =this.handleOnChange.bind(this);
        this.handleFetchEmpID = this.handleFetchEmpID.bind(this);
    }
    
    handleFetchEmpID(){
        console.log('empID',this.state.empid);
        if ( this.state.empid === '')
        {
            alert('Emp ID is required')            ;
        }
        else{
            console.log('Before get empi id');
            /*axios.request({
                method: 'POST',
                url: `http://localhost:5000/api/employee`,
                params: {
                    empId : this.state.empid,
                },
                headers:{
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
              }).then((res)=>{  
                console.log("api call sucessfull",res);
                 })
                 .then((data) => this.setState({ empDetailsRead : data }))
                 .catch((err)=>{
                console.log("api call unsucessfull",err);
                 });*/
                axios.get('/api/employee', {
                        'params': { 
                            empid: this.state.empid
                             }
                            }
                    )
                    .then(res => {console.log('get response: ',res);})
                    .then(res => {
                        console.log('res.data : ',res.data)
                    })
                    .then(data => console.log('response data : ',data))
                    .then((data) => this.setState({ empDetailsRead : data }))
                .catch(error => {
                    console.log(error);
                });
                console.log('Empid.js file response data: ', this.state.empDetailsRead);
                this.props.readEmpDetail(this.state.empDetailsRead);
        }
    }

    handleOnChange(event){
        this.setState({
            [event.target.name] : event.target.value
        })
    }

    render(){
        return(
            <div>
                <label 
                className="label" >Employee ID
                <input 
                    type="number" 
                    className="input"
                    name="empid"
                    value={this.state.empid} 
                    onChange={this.handleOnChange} />
                </label> 
                <button
                    className="button"
                    onClick={this.handleFetchEmpID}>
                    Read
                </button>   
            </div>
        );
    }
}

export default EmployeeID;
