import React,{Component} from 'react';
import { If, Then, ElseIf } from 'react-if-elseif-else-render';
import './employee.css';
import EmployeeID from './empid.js';
import axios from 'axios';
class Employee extends Component{

    constructor(){
        super()
        this.state = {
            firstName: "",
            lastName: "",
            email: "",
            phone:"",
            gender:"",
            readEmpDetails:[],
            buttontext: "Create"
        }
        this.handleCreate = this.handleCreate.bind(this);
        this.handleRead = this.handleRead.bind(this);
        this.handleUpdate = this.handleUpdate.bind(this);
        this.handleDelete = this.handleDelete.bind(this);
        this.handleOnChange = this.handleOnChange.bind(this);
        this.FinalAction = this.FinalAction.bind(this);
        this.UpdateEmployeeDetails = this.UpdateEmployeeDetails.bind(this);
    }

    handleReadEmpDetails (empDetail) {
        console.log('employee.js file handleReadEmpDetails() empDetail : ',empDetail);
        let empDetails = this.state.readEmpDetails;
        empDetails.push(empDetail);
        this.setSate({readEmpDetails : empDetails});
    }
    handleCreate(){
        this.setState({
            buttontext :   "Create"
        })
    }
    handleRead(){
        this.setState({
            buttontext :   "Read"
        })
    }
    handleUpdate(){
        this.setState({
            buttontext :   "Update"
        })
    }
    handleDelete(){
        this.setState({
            buttontext :   "Delete"
        })
    }
    handleOnChange(event){
        this.setState({
            [event.target.name] : event.target.value
        })
    }
    UpdateEmployeeDetails(){
        console.log('UpdateEmployeeDetails Inside');
        axios.put(`/api/employee/UpdateEmpDetails/1/${this.state.firstName}/${this.state.lastName}/:${this.state.email}/:${this.state.phone}/:${this.state.gender}`)
        .then (function(response){
            if (response.status === 200 || response.statusText.toLowerCase() === "ok"){
                console.log("Update Employee by Details Success");
            }else{
                console.log('Update Employee Details Failed');
            }
        })
        .catch(error => {
            console.log('UpdateEmployee By Details Failed with Error :', error);
        })
    }
    FinalAction(){
        return(
            <If condition={this.state.buttontext.toLocaleLowerCase() === "create"}>
            <Then>

            </Then>
            <ElseIf condition={this.state.buttontext.toLocaleLowerCase() === "read"}>
            <Then>
                
            </Then>
            </ElseIf>
            <ElseIf condition={this.state.buttontext.toLocaleLowerCase() === "update"}>
            <Then>
                {
                    this.UpdateEmployeeDetails()
                }
            </Then>
            </ElseIf>
            <ElseIf condition={this.state.buttontext.toLocaleLowerCase() === "delete"}>
            <Then>
                
            </Then>
            </ElseIf>

            </If>
        );
    }
    render() {
       
        return(
           <div className="main">
                <h1 
                    className="heading1"> 
                    Employee Management 
                </h1>
                <h4 
                    className="heading4"> 
                    Open Book Assignment Submitted by Gomathi T
                </h4>
                <button 
                    className="button" 
                    onClick={this.handleCreate}>
                    Create
                </button>
                <button 
                    className="button" 
                    onClick={this.handleRead}>
                    Read
                </button>
                <button 
                    className="button" 
                    onClick={this.handleUpdate}>
                    Update
                </button>
                <button 
                    className="button" 
                    onClick={this.handleDelete}>
                    Delete
                </button>
                <hr/>
                <If condition={this.state.buttontext.toLocaleLowerCase() === "create"}>
                <Then>
                    <h2 className="heading2"> Create New Employee </h2>
                    <hr className="hr" />
                </Then>
                <ElseIf condition={this.state.buttontext.toLocaleLowerCase() === "read"}>
                <Then>
                    <h2 className="heading2"> Read Existing Employee </h2>
                    <hr className="hr" />
                   <EmployeeID  readEmpDetail = {this.handleReadEmpDetails.bind(this)}/>
                    <hr />
                </Then>
                </ElseIf>
                <ElseIf condition={this.state.buttontext.toLocaleLowerCase() === "update"}>
                <Then>
                    <h2 className="heading2"> Update Existing Employee </h2>
                    <hr className="hr" />
                    <EmployeeID  />
                    <hr/>
                </Then>
                </ElseIf>
                <ElseIf condition={this.state.buttontext.toLocaleLowerCase() === "delete"}>
                <Then>
                    <h2 className="heading2"> Delete Existing Employee </h2>
                    <hr className="hr" />
                    <EmployeeID  />
                    <hr />
                </Then>
                </ElseIf>

                </If>
                    

                
                <label 
                    className="label" >First Name 
                    <input 
                        type="text" 
                        className="input"
                        name="firstName" 
                        value={this.state.firstName} 
                        onChange={this.handleOnChange} />
                </label>
                <br />
                <label 
                    className="label">Last Name   
                    <input 
                        type="text" 
                        name="lastName" 
                        className="input"
                        value={this.state.lastName} 
                        onChange={this.handleOnChange} />
                </label>
                <br />
                <label 
                    className="label">Email  
                    <input 
                        type="email" 
                        name="email" 
                        className="input"
                        value={this.state.email} 
                        onChange={this.handleOnChange} />
                </label>
                <br />
                <label 
                    className="label">Phone  
                    <input 
                        type="tel" 
                        name="phone" 
                        className="input"
                        value={this.state.phone} 
                        onChange={this.handleOnChange} />
                </label>
                <br />
                <label 
                    className="label">Gender  </label>
                <label
                    className="label">    
                    <input 
                        type="radio" 
                        name="gender" 
                        className="radio"
                        value="male" 
                        checked={this.state.gender === "male"}
                        onChange={this.handleOnChange}/> Male
                </label>
                <label
                    className="label">    
                    <input 
                        type="radio" 
                        name="gender" 
                        className="radio"
                        value="female" 
                        checked={this.state.gender === "female"}
                        onChange={this.handleOnChange}/> Female
                </label>
            <br />
            <If condition ={this.state.buttontext.toLocaleLowerCase() !== "read"}>
            <Then>
            {
                <button 
                    className="button"
                    onClick={this.FinalAction}>{this.state.buttontext}
                </button>
            }
            </Then>
            </If>
           </div> 
        );
    }
}


export default Employee;
